<?php    /**     * Created by PhpStorm.     * User: ontheroad     * Date: 2017/8/8     * Time: 16:05     */    namespace App\Mailer;    use Illuminate\Support\Facades\Mail;    use Naux\Mail\SendCloudTemplate;    /**     * Class Mailer     * @package App\Mailer     */    class Mailer    {        /**         * @param $template 模版文件         * @param $email  接收邮箱         * @param array $data 发送相关配置及数据         */        protected function sendTo ($template,$email,array $data)        {            //$data = ['url' => url('http://127.0.0.1:8000', Auth::guard('api')->user()->name)];            $content = new SendCloudTemplate($template, $data);            Mail::raw($content, function ($message) use ($email){                $message->from('1178711258@qq.com', 'onTheRoad');                $message->to($email);            });        }    }