<?php    /**     * Created by PhpStorm.     * User: ontheroad     * Date: 2017/8/14     * Time: 13:13     */    namespace App\Repositories;    use App\Topic;    use Illuminate\Http\Request;    /**     * Class TopicRepository     * @package App\Repositories     */    class TopicRepository    {        /**         * @param Request $request         * @return mixed         */        public function getTopicForTagging (Request $request)        {            $topics = Topic::select(['id','name'])                ->where('name','like','%'.$request->query('q').'%')                ->get();            return $topics;        }    }