<template>    <div>        <button class="btn btn-default"                @click="showSendMessageForm">发送私信        </button>        <div class="modal fade" id="modal-send-message" tabindex="-1" role="dialog">            <div class="modal-dialog">                <div class="modal-content">                    <div class="modal-header">                        <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>                        <h4 class="modal-title">                            发送私信                        </h4>                    </div>                    <div class="modal-body">                        <textarea name="msg_content" class="form-control" id="msg_content" v-model="msg_content" v-if="!status" cols="10" rows="5" required placeholder="输入私信内容"></textarea>                        <div class="alert alert-success" v-if="status">                            <p>私信发送成功！</p>                        </div>                    </div>                    <div class="modal-footer">                        <button type="button" class="btn btn-default" data-dismiss="modal">关闭</button>                        <button type="button" class="btn btn-primary" @click="store">                            发送私信                        </button>                    </div>                </div>            </div>        </div>    </div></template><script>    export default {        props:['user'],        data() {            return {                msg_content:'',                status: false            }        },        methods: {            store() {                this.$http.post('/api/message/store',{'user': this.user,'msg_content':this.msg_content }).then(response => {                    //console.log(response.data);                    this.status = response.data.status                    this.msg_content = ''                    setTimeout(function () {                        $('#modal-send-message').modal('hide')                    },2000)                })            },            showSendMessageForm() {                $('#modal-send-message').modal('show');            }        }    }</script>