<template>    <button class="btn btn-default"            v-text="texts" v-on:click="follow"            v-bind:class="{'btn-success':followed}">    </button></template><script>    export default {        props:['user'],        mounted() {            this.$http.get('/api/user/followers/'+ this.user).then(response => {                //console.log(response.data);                this.followed = response.data.followed            })        },        data() {            return {                followed: false            }        },        computed: {            texts() {                return this.followed ? '已关注' : '关注Ta'            }        },        methods: {            follow() {                this.$http.post('/api/user/follow',{'user': this.user }).then(response => {                    //console.log(response.data);                    this.followed = response.data.followed                })            }        }    }</script>